require_once dirname(__DIR__).'/vendor/autoload.php';

$input = read_input($argv, __FILE__, __COMPILER_HALT_OFFSET__);
$up = mb_strlen(str_replace('(', '', $input));
$down = mb_strlen(str_replace(')', '', $input));

echo 0 - $up + $down;
echo PHP_EOL;

$floor = 0;
$firstNegative = -1;

foreach (str_split($input) as $key => $value) {
    $floor = ('(' === $value) ? $floor + 1 : $floor - 1;

    if ($floor < 0) {
        $firstNegative = $key + 1;
        break;
    }
}

echo $firstNegative;
echo PHP_EOL;

__halt_compiler();
