require_once dirname(__DIR__).'/vendor/autoload.php';

define('UP', 0);
define('RIGHT', 1);
define('DOWN', 2);
define('LEFT', 3);

$input = read_input($argv, __FILE__, __COMPILER_HALT_OFFSET__);
$rows = explode("\n", $input);
$rows = array_map(static function (string $row): array {
    return str_split($row);
}, $rows);

$y = 0;
$x = array_search('|', $rows[0], true);
$direction = DOWN;
$steps = 1;
$letters = [];

while (true) {
    ++$steps;

    switch ($direction) {
        case UP:
            --$y;
            break;
        case RIGHT:
            ++$x;
            break;
        case DOWN:
            ++$y;
            break;
        case LEFT:
            --$x;
            break;
    }

    if ('|' !== $rows[$y][$x] && '-' !== $rows[$y][$x]) {
        if (1 === preg_match('/^[A-Z]$/i', $rows[$y][$x])) {
            $letters[] = $rows[$y][$x];
        }

        $possibilities = array_filter([
            UP => DOWN !== $direction ? [$y - 1, $x] : null,
            RIGHT => LEFT !== $direction ? [$y, $x + 1] : null,
            DOWN => UP !== $direction ? [$y + 1, $x] : null,
            LEFT => RIGHT !== $direction ? [$y, $x - 1] : null,
        ]);

        foreach ($possibilities as $direction => $possibility) {
            if (true === isset($rows[$possibility[0]][$possibility[1]]) &&
                ' ' !== $rows[$possibility[0]][$possibility[1]]) {
                continue 2;
            }
        }

        break;
    }
}

echo implode('', $letters).PHP_EOL;
echo $steps.PHP_EOL;

__halt_compiler();
